mapper.select:
  SELECT * 
FROM( 
        SELECT ROWNUM no, t.* 
        FROM( 
                 SELECT to_number(REPLACE(freq_code, 'fq_', '')) AS order_num,  freq_code, freq_name, freq_cont  
                FROM freq_question 
                ORDER BY order_num DESC 
            ) t 
     ) m 
 WHERE m.no BETWEEN #{currentPage} AND #{numberPerPage}
